sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Madifying the "Active" filed based on the value of "Related Data Migration Record (if any)"
    if (g_form.getValue("u_related_data_migration_record") != "") {
        g_form.setValue("active", false);
    } else {
        g_form.setValue("active", true);
    }
}
